import {
  Button,
  Card,
  Checkbox,
  Container,
  Grid,
  Paper,
  Slide,
  TextField,
  Typography,
} from "@mui/material";
import { makeStyles } from "@mui/styles";
import { useState } from "react";
import { toast } from "react-toastify";
import baseUrl from "../api";
import img from "../components/media/library3.jpeg";

const useStyles = makeStyles({
  header: {
    marginLeft: "0",
    fontSize: "1.5rem",
  },
  center: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    paddingTop: "2rem",
  },
  billing: {
    width: "40%",
    margin: "0 2rem 2rem 2rem",
  },
  creditcard: {
    width: "40%",
    marginRight: "2rem",
  },
  shipping: {
    width: "80%",
    marginRight: "1.3rem",
    paddingBottom: "2rem",
  },
  btn: {
    display: "flex",
    justifyContent: "center",
    margin: "2rem",
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "1fr 1fr",
    justifyItems: "end",
  },
});

function CheckoutForm(props) {
  const classes = useStyles();
  const [billingFirstName, setBillingFirstName] = useState("");
  const [billingLasttName, setBillingLasttName] = useState("");
  const [billingAddresLine1, setBillingAddresLine1] = useState("");
  const [billingAddressLine2, setBillingAddressLine2] = useState("");
  const [billingZip, setBillingZip] = useState("");
  const [billingCity, setBillingCity] = useState("");
  const [billingState, setBillingState] = useState("");
  const [billingCountry, setBillingCountry] = useState("");

  const [same, setSame] = useState(false);
  const [shippingFirstName, setshippingFirstName] = useState("");
  const [shippingLasttName, setshippingLasttName] = useState("");
  const [shippingAddresLine1, setshippingAddresLine1] = useState("");
  const [shippingAddressLine2, setshippingAddressLine2] = useState("");
  const [shippingZip, setshippingZip] = useState("");
  const [shippingCity, setshippingCity] = useState("");
  const [shippingState, setshippingState] = useState("");
  const [shippingCountry, setshippingCountry] = useState("");

  const completeOrder = async () => {
    console.log(props.cart.id);
    const response = await fetch(`${baseUrl}/orders/${props.cart.id}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${props.token}`,
      },
      body: JSON.stringify({
        total: props.total,
        date: props.purchaseDate,
      }),
    });
    const info = await response.json();
    console.log("working");
  };

  const sameAsShipping = () => {
    if (same) {
      setshippingFirstName(billingFirstName);
      setshippingLasttName(billingLasttName);
      setshippingAddresLine1(billingAddresLine1);
      setshippingAddressLine2(billingAddressLine2);
      setshippingZip(billingZip);
      setshippingCity(billingCity);
      setshippingState(billingState);
      setshippingCountry(billingCountry);
    }
  };

  return (
    <>
      <div
        style={{
          backgroundImage: `url(${img})`,
          backgroundRepeat: "repeat",
          minHeight: "84vh",
          height: "100%",
          zIndex: "-2",
          // marginTop: "-3em",
          height: "100vh",
          fontFamily: "Nunito",
        }}
      >
        <br></br>
        <h1
          style={{
            textAlign: "center",
            fontFamily: "satisfy",
            fontSize: "3em",
            fontWeight: "700",
          }}
        >
          Checkout
        </h1>

        <Container
          className={classes.root}
          component="main"
          maxWidth="s"
          style={{
            width: "80%",
            borderRadius: "10px",
            backgroundColor: "#FBFBFD",
            marginBottom: "2rem",
          }}
        >
          <form
            noValidate
            autoComplete="off"
            className={classes.form}
            //   onSubmit={completeOrder}
          >
            <div className={classes.center}>
              <div className={classes.billing}>
                <Typography className={classes.header}>
                  Billing Information
                </Typography>
                <Grid container spacing={2}>
                  <Grid item xs={12} sm={6}>
                    <TextField
