import { Link } from "react-router-dom";
import "./Components.css";
import { Button } from "@mui/material";
import { useEffect, useState } from "react";
import baseUrl from "../api";

const ProductPage = (props) => {
  const [book, setBook] = useState("");

  const getBookInfo = async () => {
    const response = await fetch(`${baseUrl}${window.location.pathname}`);
    const info = await response.json();
    setBook(info);
  };

  useEffect(() => {
    getBookInfo();
  }, []);

  console.log(book);

  return (
    <Card raised>
      <CardContent>
        <CardMedia
          component="img"
          image={book.image}
          // height='200px'
        />
        <br></br>
        <Typography align="center"> {book.name}</Typography>
        <Typography variant="subtitle2"> {book.description}</Typography>
        <Typography align="right">${book.price}</Typography>
        {/* <Typography> {book.stock}</Typography> */}
      </CardContent>
      <CardActions>
        {/* <Button
                      size="small"
                      onClick={(e) => {
                        addToCart(book.id);
                      }}
                    >
                      Add to Cart
                    </Button> */}
      </CardActions>
    </Card>
  );
};

export default ProductPage;
