import { Link } from "react-router-dom";
import "./Components.css";
import { Button } from "@mui/material";

const Admin = (props) => {
  return (
    <>
      <h1>Admin</h1>

      <form onSubmit={addNewRoutine}>
        <input
          className="addActivityInput"
          placeholder="Enter routine name"
          type="text"
          value={newRoutine}
          onChange={(e) => setNewRoutine(e.target.value)}
          required
        ></input>
        <input
          className="addActivityInput"
          type="text"
          size="50"
          placeholder="Enter routine goal"
          value={routineGoal}
          onChange={(e) => setRoutineGoal(e.target.value)}
        ></input>
        <input
          value={routinePublic}
          type="checkbox"
          onChange={(e) => {
            !routinePublic ? setRoutinePublic(true) : setRoutinePublic(false);
          }}
        />
        <label> Make Routine Public? </label>
        <button style={{ marginLeft: "10px" }}>Add New Routine!</button>
        <p style={{ color: "red" }}>{errorMessage}</p>
      </form>
    </>
  );
};

export default Admin;
