import { Link } from "react-router-dom";
import "./Components.css";
import {
  Button,
  Card,
  CardActions,
  CardContent,
  CardMedia,
  Typography,
} from "@mui/material";
import { useEffect, useState } from "react";
import baseUrl from "../api";
import { ThumbUp } from "@mui/icons-material";

const ProductPage = (props) => {
  const [book, setBook] = useState("");
  const [quantity, setQuantity] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const getBookInfo = async () => {
    const response = await fetch(`${baseUrl}${window.location.pathname}`);
    const info = await response.json();
    setBook(info);
  };

  useEffect(() => {
    getBookInfo();
  }, []);

  const addItemToCart = async (e) => {
    e.preventDefault();
    if (!quantity) {
      quantity = 1;
    }
    console.log(
      props.cart.id,
      window.location.pathname.substring(10),
      quantity
    );
    const response = await fetch(
      `${baseUrl}/orders/${props.cart.id}/products`,
      {
        method: "POST",
        headers: {
          Authorization: `Bearer ${props.token}`,
        },
        body: {
          product_id: window.location.pathname.substring(10),
          quantiy: quantity,
        },
      }
    );
    const info = await response.json();
    console.log(info);
    if (info.error) {
      console.log(info.error);
      return setErrorMessage(info.error);
    }
  };

  return (
    <div
      style={{
        marginTop: "12em",
        marginLeft: "4rem",
        marginRight: "4rem",
        display: "grid",
        gridTemplateColumns: "50% 50%",
      }}
    >
      <img src={book.image}></img>
      <div>
        <h2>{book.name}</h2>
        <div>by {book.author}</div>
        <br></br>
        <div style={{ borderBottom: "1px solid black", paddingBottom: "10px" }}>
          {book.description}
        </div>
        <div style={{ marginTop: "2rem" }}>
          {book.stock && (
            <div>
              <ThumbUp style={{ color: "green", marginRight: "1rem" }} />
              <span> In Stock - {book.stock} available</span>
            </div>
          )}
          {!book.stock && <div style={{ color: "red" }}> Out of Stock</div>}
          <div style={{ marginTop: "1em" }}>${book.price}</div>
          <form style={{ marginTop: "1em" }}>
            <select
              onChange={(e) => {
                setQuantity(e.target.value);
              }}
            >
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4</option>
              <option value="5">5</option>
              <option value="6">6</option>
              <option value="7">7</option>
              <option value="8">8</option>
              <option value="9">9</option>
              <option value="10">10</option>
            </select>{" "}
            <br></br>
            <button onClick={addItemToCart}>Add to Cart</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
